mapper.select:
select p1.id as id , p1.prd_no as prdNo, p1.prd_name as prdName, p1.prd_code as prdCode, p1.tp_cd as prdType, p1.sup_id as supId, p1.sh_date as shDate, p1.img_urls as imgUrl, p1.is_gifts as isGifts, p1.is_sales as isSales, p1.sale_code as saleCode, p1.is_return as isReturn, p1.is_used as isUsed, p1.inst_data as instDate, p1.inst_id as instId, p1.mdf_date as mdfDate, p1.mdf_id as mdfId, p2.tp_name as tpName, s.sup_name as supName, s.sup_comp as supComp from pro_info p1
left JOIN prd_type p2 on p2.tp_cd = p1.tp_cd LEFT join supplier s on s.id = p1.sup_id WHERE 1=1
and p1.prd_no = #{prdNo}

select p1.id , p1.prd_no as prdNo, p1.prd_name as prdName, p1.prd_code as prdCode, p1.tp_cd as prdType, p1.sup_id as supId, p1.sh_date as shDate, p1.img_urls as imgUrl, p1.is_gifts as isGifts, p1.is_sales as isSales, p1.sale_code as saleCode, p1.is_return as isReturn, p1.is_used as isUsed, p1.inst_data as instDate, p1.inst_id as instId, p1.mdf_date as mdfDate, p1.mdf_id as mdfId, p2.tp_name as tpName, s.sup_name as supName, s.sup_comp as supComp from pro_info p1
left JOIN prd_type p2 on p2.tp_cd = p1.tp_cd LEFT join supplier s on s.id = p1.sup_id WHERE 1=1
and p1.prd_no LIKE concat('%',#{prdNo},'%')
and p1.prd_name LIKE concat('%',#{prdName},'%')
and p1.prd_code LIKE concat('%',#{prdCode},'%')
and p1.is_sales = #{isSales}
and p1.is_return = #{isReturn}
and p2.tp_cd = #{prdType}
and p1.inst_data >= #{instDate1}
and p1.inst_data <= #{instDate1 }
and p1.sale_code = #{saleCode}
and p1.is_used = #{isUsed}
and s.id = #{supId}
and p1.is_gifts = #{isGifts}
order by id asc
