mapper.select:
select b.*,c.*,pro_info.prd_name
from prd_detail AS b
LEFT JOIN (select a.prd_dtl_no,a.prd_no,SUM(a.w_count) as totalcount from prodetail_ware_relation as a GROUP BY prd_dtl_no,a.prd_no) as c ON c.prd_dtl_no=b.prd_dtl_id
LEFT JOIN pro_info ON b.prd_id=pro_info.prd_no
WHERE 1=1

    and prd_name = #{prd_name}


    and prd_dtl_name = #{prd_dtl_name}



    and sale_price  >=  #{price_min}


    and sale_price  <=  #{price_max}















                and b.is_used = 'n'

             and b.is_used = 'y'


    and b.is_used = 'y'







order by b.prd_id

mapper.update:
UPDATE
        prd_detail AS a
SET
      a.prd_dtl_name=#{prd_dtl_name}
    ,a.img_url=#{img_url},a.sale_price=#{sale_price}
WHERE
    a.prd_dtl_id=#{prd_dtl_id}

mapper.select:
UPDATE prodetail_ware_relation
  SET w_count=#{w_count}
    WHERE w_no=#{w_no}
      AND prd_dtl_no=#{prd_dtl_no}
