mapper.select:
        SELECT
        pd.*,pi.prd_name,pwr.w_count,pwr.is_used,ware.w_name
        FROM
        prd_detail AS pd,pro_info AS pi,prodetail_ware_relation AS pwr,ware
#         LEFT JOIN pro_info
#         ON prd_id=pro_info.prd_no
#         LEFT JOIN prodetail_ware_relation
#         ON prd_id=prodetail_ware_relation.prd_no
#         LEFT JOIN ware
#         ON prd_detail.ware_id=ware.w_no
        WHERE pd.prd_dtl_id=pwr.prd_dtl_no AND ware.w_no=pwr.w_no AND pi.prd_no=pwr.prd_no

            and prd_name = #{prd_name}


            and prd_dtl_name = #{prd_dtl_name}



            and sale_price  >=  #{price_min}


            and sale_price  <=  #{price_max}


            and w_name = #{w_name}


            and w_count  >=  #{count_min}


            and w_count  <=  #{count_max}






                        and pwr.is_used = 'n'

                     and pwr.is_used = 'y'


            and pwr.is_used = 'y'




            and pwr.is_used = #{is_used}


        order by pd.prd_id

mapper.update:
UPDATE
        prd_detail AS a,prodetail_ware_relation AS b
SET
      a.prd_dtl_name=#{prd_dtl_name}
    ,a.img_url=#{img_url},a.sale_price=#{sale_price},b.w_count=#{w_count},
    b.w_no=#{w_no},b.is_used=#{isUsed}
WHERE
    a.prd_dtl_id=#{prd_dtl_id}
AND
    a.prd_dtl_id=b.prd_dtl_no
